void TMR1_IRQHandler() // Timer1 interrupt subroutine 
{
    if (m == 1){
			   PWM4_Freq(Tone_Freq(mstart[(i++)%55]),duty_cycle); // set PWM0 with frequency & duty cycle
			   if (i%55 == 54){
			       m = 0;
				     PWM4_Freq(0,duty_cycle); // set PWM0 with frequency & duty cycle
				 }
			}
			else if (m == 2){
				 PWM4_Freq(Tone_Freq(mcatch[(i++)%3]),duty_cycle); // set PWM0 with frequency & duty cycle
				 if (i%3 == 2){
			       m = 0;
				     PWM4_Freq(0,duty_cycle); // set PWM0 with frequency & duty cycle
				 }
			}
			else{
				PWM4_Freq(0,duty_cycle); // set PWM0 with frequency & duty cycle
			}
		TIMER1->TISR.TIF =1;
}
void TMR2_IRQHandler() // Timer1 interrupt subroutine 
{
		old_y3++;
		if(old_y3 == 8)
			old_y3 = 1;
		if(start)
			gametime++;
		if(final)
			savetime++;
	TIMER2->TISR.TIF =1;
}
void TMR3_IRQHandler() // Timer1 interrupt subroutine 
{
	rand = (rand+8)%128;
	TIMER3->TISR.TIF =1;
}
